'use client';


import {
  Button as ButtonPrimitive,
  composeRenderProps,
  Disclosure as AccordionItemPrimitive,
  DisclosureGroup as AccordionPrimitive,
  type DisclosureGroupProps as AccordionPrimitiveProps,
  DisclosurePanel as AccordionContentPrimitive,
  type DisclosurePanelProps as AccordionContentPrimitiveProps,
  type DisclosureProps as AccordionItemPrimitiveProps,
  Heading as HeadingPrimitive,
} from 'react-aria-components';

import { Icons } from '@/components/ui/icons';

// Accordion (DisclosureGroup)
type AccordionProps = AccordionPrimitiveProps;

function Accordion({ className, ...props }: AccordionProps) {
  return (
    <AccordionPrimitive
      className={`accordion ${className ?? ''}`}
      data-slot="accordion"
      {...props}
    />
  );
}

// AccordionItem (Disclosure)
type AccordionItemProps = AccordionItemPrimitiveProps;

function AccordionItem({ className, ...props }: AccordionItemProps) {
  return (
    <AccordionItemPrimitive
      className={composeRenderProps(className, (className) =>
        `accordion-item ${className ?? ''}`.trim(),
      )}
      data-slot="accordion-item"
      {...props}
    />
  );
}

// AccordionTrigger
type AccordionTriggerProps = {
  children: React.ReactNode;
  className?: string;
};

function AccordionTrigger({ children, className }: AccordionTriggerProps) {
  return (
    <HeadingPrimitive className="accordion-heading">
      <ButtonPrimitive
        className={`accordion-trigger focus-ring ${className ?? ''}`}
        data-slot="accordion-trigger"
        slot="trigger"
      >
        {children}
        <Icons.ChevronDown aria-hidden className="accordion-chevron" />
      </ButtonPrimitive>
    </HeadingPrimitive>
  );
}

// AccordionContent (DisclosurePanel)
type AccordionContentProps = AccordionContentPrimitiveProps;

function AccordionContent({
  children,
  className,
  ...props
}: AccordionContentProps) {
  return (
    <AccordionContentPrimitive
      className={composeRenderProps(className, (className) =>
        `accordion-content ${className ?? ''}`.trim(),
      )}
      data-slot="accordion-content"
      {...props}
    >
      <div className="accordion-content-inner">{children}</div>
    </AccordionContentPrimitive>
  );
}

export { Accordion, AccordionContent, AccordionItem, AccordionTrigger };
export type {
  AccordionContentProps,
  AccordionItemProps,
  AccordionProps,
  AccordionTriggerProps,
};

/* Accordion */
.accordion {
  width: 100%;
}

/* Accordion Item */
.accordion-item {
  border-bottom: 1px solid var(--border);
}

.accordion-item:last-child {
  border-bottom: none;
}

/* Accordion Heading */
.accordion-heading {
  display: flex;
}

/* Accordion Trigger */
.accordion-trigger {
  display: flex;
  flex: 1;
  align-items: flex-start;
  justify-content: space-between;
  gap: 1rem;
  padding-block: 1rem;
  border-radius: var(--radius-md);
  font-size: 0.875rem;
  font-weight: 500;
  text-align: left;
  cursor: default;
  transition: all 150ms ease;
  background: transparent;
  border: none;
  color: inherit;
}

.accordion-trigger:hover {
  text-decoration: underline;
}

.accordion-trigger[data-disabled] {
  pointer-events: none;
  opacity: 0.5;
}

/* Accordion Chevron */
.accordion-chevron {
  flex-shrink: 0;
  color: var(--muted-foreground);
  pointer-events: none;
  transform: translateY(0.125rem);
  transition: transform 200ms ease;
}

[data-expanded] .accordion-chevron {
  transform: translateY(0.125rem) rotate(180deg);
}

/* Accordion Content */
.accordion-content {
  overflow: hidden;
  font-size: 0.875rem;
  height: var(--disclosure-panel-height);
  transition: height 200ms ease-out;
}

.accordion-content-inner {
  padding-bottom: 1rem;
}
